    public function name2id($city_name) {
        $city_id = $this->city_list[$city_name];
        $city_names = $this->weather_city_index_array[substr($city_id, 0, 2)] . "_" . $this->weather_city_index_array[substr($city_id, 0, 4)] . "_" . $this->weather_city_index_array[$city_id];
