    /**
     * 使用城市名调用
     */
    public function getWeather($city_name) {
        if (!$this->cityName)
            $this->cityName = $city_name;
        if (!$this->cityId) {
            $city_id = $this->city_list[$this->cityName];
            $city_names = $this->weather_city_index_array[substr($city_id, 0, 2)] . "_" . $this->weather_city_index_array[substr($city_id, 0, 4)] . "_" . $this->weather_city_index_array[$city_id];
            $this->cityId = $city_names;
        }
        return $this->getWeatherById($this->cityId);
    }

    /**
     * 使用城市ID调用
     */
    public function getWeatherById($city_id, $encode = true) {
        if (!$this->cityId)
            $this->cityId = $city_id;
        $cacheArray = $this->cacheWeather();
        //根据时间判断，天气预报18:00 - 08:00 不更新所以 00:00 之后应该读取weather2
        //time : 201403101800
        $flag = date("Ymd", time()) > substr($cacheArray['time'], 0, 8);
        if ($flag) {
            $start = 2;
            $count = 0;
            $cacheBackArray = $cacheArray;
            for ($i = $start; $i <= 5; $i++) {
                $j = $i - 1;
                //只放4个数据，否则会出错
                if ($count == 4)
                    continue;
                $cacheArray["weather" . $j . "_d"] = $cacheBackArray["weather" . $i . "_d"];
                $cacheArray["weather" . $j . "_n"] = $cacheBackArray["weather" . $i . "_n"];
                $cacheArray["weather" . $j] = $cacheBackArray["weather" . $i];
                $cacheArray["temp" . $j] = $cacheBackArray["temp" . $i];
                $cacheArray["wind" . $j . "_d"] = $cacheBackArray["wind" . $i . "_p"];

            }
            date_default_timezone_set('PRC');
            $is_night = (date("G") > 18 || date("G") < 6) ? true : false;
            $cacheArray["icon1"] = $this->getWeatherIcon(1, $is_night, $cacheArray);
            $cacheArray["icon2"] = $this->getWeatherIcon(2, $is_night, $cacheArray);
            $cacheArray["icon3"] = $this->getWeatherIcon(3, $is_night, $cacheArray);
            $cacheArray["bg"] = $this->getWeatherBg($is_night, $cacheArray);
            $cacheArray["bg1"] = $cacheArray['weather1_d'];
        }
        if (is_array($cacheArray))
            return $encode ? CJSON::encode($cacheArray) : $cacheArray;
        else
            return $cacheArray;
    }

    /**
     * 获取天气原始函数,带缓存，重试两次
     */
    public function cacheWeather() {
        if (strpos($this->cityId, "_") === FALSE) {
            return "无该城市的天气数据";
        }

        try {
            $weather_array = Yii::app()->cache->get("C_WEATHER_" . bin2hex($this->cityId));
            if (is_array($weather_array)) {
                return $weather_array;
            }
        } catch (Exception $e) {
            
        }

        $weather_array = $this->getWeatherOnline();

        if (!is_array($weather_array)) {
            $weather_array = $this->getFromTongda();
        }
        if (!is_array($weather_array)) {
            return $weather_array;
        }
        Yii::app()->cache->set("C_WEATHER_" . bin2hex($this->cityId), $weather_array, $weather_array['cache_expire_time']);
        return $weather_array;
    }

    /**
     * 获取天气原始函数,带缓存，重试两次
     */
    public function getMobileWeather($city_id) {
        $this->cityId = $city_id;
        $weather_array = $this->getWeatherOfficial();
        while (list($key, $value) = each($weather_array))
            $weather_array[$key] = $value;
        $flag = date("Ymd", time()) > substr($weather_array['time'], 0, 8);
        $mobile = array();
        //根据时间判断，天气预报18:00 - 08:00 不更新所以 00:00 之后应该读取weather2
        //time : 201403101800
        if ($flag) {
            //气温
            $TEMPERATURE = $weather_array["temp2"];
            //天气
            $WEATHER = $weather_array["weather2"];
            //风
            $WIND = $weather_array["wind2_d"];
            //图例
            $IMG1 = $weather_array["weather2_d"];
            $IMG2 = $weather_array["weather2_n"];
        } else {
            //气温
            $TEMPERATURE = $weather_array["temp1"];
            //天气
            $WEATHER = $weather_array["weather1"];
            //风
            $WIND = $weather_array["wind1_d"];
            //图例
            $IMG1 = $weather_array["weather1_d"];
            $IMG2 = $weather_array["weather1_n"];
        }
        $START = $flag ? 2 : 1;
        $COUNT = 0;
        for ($I = $START; $I <= 5; $I++) {
            //只放4个数据，否则会出错
            if ($COUNT == 4)
                continue;

            $TIME = time() + 86400 * ($COUNT);
            $IMG1 = $weather_array["weather" . $I . "_d"];
            $IMG2 = $weather_array["weather" . $I . "_n"];
            $WEATHER = $weather_array["weather" . $I];
            $TEMPERATURE = $weather_array["temp" . $I];
            $WIND = $weather_array["wind" . $I . "_p"];
            //for mobile data
            $weather_mobile = array();
            $_WEATHER_MOBILE = array();

            if ($COUNT == 0)
                $weather_mobile['date'] = date("n月j日", $TIME);
            else if ($COUNT == 1)
                $weather_mobile['date'] = '明天';
            else
                $weather_mobile['date'] = $this->getWeekDay($TIME);

            $weather_mobile['img'] = $this->weatherIconMobile[$IMG1]['s'];
            $weather_mobile['weather'] = $WEATHER;
            $weather_mobile['temperature'] = $TEMPERATURE;
            $weather_mobile['wind'] = $WIND;

            if ($COUNT == 0) {
                $weather_mobile['bg'] = $this->weatherIconMobile[$IMG1]['s'];
            }
            $mobile[] = $weather_mobile;

            $COUNT++;
        }
        return $mobile;
    }

