    function getFromTongda() {
        $sys_version = Yii::app()->core->version;
        $weather_array = self::WEATHER_API_TONGDA . "?CITY_ID=" . $this->cityId . "&VER=" . $sys_version;
        $html = @file_get_contents($weather_array);
        if ($html === false)
            return "下载天气数据失败(01)";

        $weather_array = @unserialize($html);
        if ($weather_array === FALSE)
            return "解析天气数据失败(02)";

        if (count($weather_array) == 0)
            return _("该城市天气信息为空");

        return $weather_array;
    }

    /**
     * 获取天气函数，对原始数据进行处理
     */
    protected function getWeatherOnline() {
        $arr_html = $this->getWeatherOfficial();
        if (!is_array($arr_html))
            return $arr_html;
        $weather_array = array();
        $key_array = array(//四天信息
            'time',
            'cache_time',
            'cache_expire_time',
            'city',
            'city_en',
            'date_y',
            'temp1', 'temp2', 'temp3', 'temp4','temp5',
            'weather1', 'weather2', 'weather3', 'weather4','weather5',
            'weather1_d', 'weather1_n', 'weather2_d', 'weather2_n',
            'weather3_d', 'weather3_n', 'weather4_d', 'weather4_n', 
            'weather5_d', 'weather5_n',
            'wind1_d', 'wind2_d', 'wind3_d', 'wind4_d','wind4_d',
            'wind1_p', 'wind2_p', 'wind3_p', 'wind4_p','wind5_p',
            'index',
            'index_d',
        );
        while (list($key, $value) = each($arr_html)) {
            if (!in_array($key, $key_array))
                continue;

            $weather_array[$key] = $value;
        }

        if (count($weather_array) == 0)
            return "该城市天气信息为空(03)";
        else {
            date_default_timezone_set('PRC');
            $is_night = (date("G") > 18 || date("G") < 6) ? true : false;
            $weather_array["icon1"] = $this->getWeatherIcon(1, $is_night, $weather_array);
            $weather_array["icon2"] = $this->getWeatherIcon(2, $is_night, $weather_array);
            $weather_array["icon3"] = $this->getWeatherIcon(3, $is_night, $weather_array);
            $weather_array["bg"] = $this->getWeatherBg($is_night, $weather_array);
            $weather_array["bg1"] = $weather_array['weather1_d'];
        }

        return $weather_array;
    }

    /**
     * 获取天气背景
     */
    protected function getWeatherBg($is_night, $weather_array) {
        $img_id_use = $is_night ? "1_n" : "1_d";
        $key = $weather_array["weather" . $img_id_use];
        //晴天
        $index = 0;
        //多云天
        if (in_array($key, array(1)))
            $index = 1;
        //阴天
        elseif (in_array($key, array(2)))
            $index = 2;
        //小到中雨天
        elseif (( $key >= 6 && $key <= 8 ) || $key == 19 || ( $key >= 21 && $key <= 22 ))
            $index = 3;
        //大到暴雨
        elseif (( $key >= 9 && $key <= 11 ) || ( $key >= 23 && $key <= 25 ))
            $index = 4;
        //雪天
        elseif (( $key >= 13 && $key <= 15 ) || ( $key >= 26 && $key <= 27 ))
            $index = 5;
        //大到暴雪
        elseif (( $key >= 16 && $key <= 17 ) || $key == 28)
            $index = 6;
        //沙尘天
        elseif (in_array($key, array(20, 29, 30, 31)))
            $index = 7;
        //雷雨天
        elseif (in_array($key, array(4, 5)))
            $index = 8;
        //雾霾天
        elseif (in_array($key, array(18, 53)))
            $index = 9;

        return $this->weatherBg["bg" . $index];
    }

    /**
     * 获取天气图标
     */
    protected function getWeatherIcon($img_id, $is_night, $weather_array) {
        $img_id_use = $is_night ? ($img_id . "_n") : ($img_id . "_d");
        $icon_index = $weather_array["weather" . $img_id_use];
        //不再区分白天黑夜
        $icon_array = $is_night ? $this->weatherIconNight : $this->weatherIcon;

        if ($icon_index > count($icon_array)) {
            $icon = $icon_array["00"];
        } else {
            $icon = $icon_array[$icon_index];
        }
        return $icon;
    }

    protected function getDailyWeatherOnline() {
        /* {"weatherinfo":{"city":"西安","cityid":"101110101","temp":"24","WD":"西风","WS":"2级","SD":"19%","WSE":"2","time":"12:00","isRadar":"1","Radar":"JC_RADAR_AZ9290_JB"}} */
        $time = time();
        $params = array(
            'type' => 'forecast3d',
            'date' => $time,
            'sys_version' => Yii::app()->core->version,
            'token' => TUtil::authcode($time, 'ENCODE', 'tdweatherservices'),
            'api_ver' => '2.0',
            'city_name' => $this->cityId,
            'stype' => 'goa'
        );
        $weather_url = self::WEATHER_API_OFFICAL . "?" . http_build_query($params);
        $html = @file_get_contents($weather_url);

        if ($html === false)
            return "下载天气数据失败(01)";

        $arr_html = json_decode($html, true);

        if (!is_array($arr_html))
            return "解析天气数据失败(02)";

        $weather_array = array();
        $key_array = array(//实时信息
            'city',
            'temp',
            'WD',
            'WS',
            'time',
        );
        while (list($key, $value) = each($arr_html)) {
            if (!in_array($key, $key_array))
                continue;

            $weather_array[$key] = $value;
        }

        if (count($weather_array) == 0)
            return "该城市天气信息为空(03)";

        return $weather_array;
    }

    protected function getDailyWeatherCurrent() {
        $weather_array = array();
        $weather_array = Yii::app()->cache->get("C_WEATHER_" . bin2hex($this->cityId));
        //有缓存则返回
        if (is_array($weather_array))
            return $weather_array;
        $time = time();
        $params = array(
            'type' => 'forecast3d',
            'date' => $time,
            'sys_version' => Yii::app()->core->version,
            'token' => urlencode(TUtil::authcode($time, 'ENCODE', 'tdweatherservices')),
            'api_ver' => '2.0',
            'city_name' => $this->cityId,
            'stype' => 'goa'
        );
        $weather_url = self::WEATHER_API_OFFICAL . "?" . http_build_query($params);
        $html = @file_get_contents($weather_url);
        if ($html === false)
            return "下载实时天气数据失败(01)";

        $arr_html = json_decode($html, true);

        if (!is_array($arr_html))
            return "解析实时天气数据失败(02)";

        $key_array = array(//实时信息
            'cache_time',
            'cache_expire_time',
            'city',
            'temp',
            'sd',
            'ws',
            'wd',
            'time',
            'day',
        );

        while (list($key, $value) = each($arr_html)) {
            if (!in_array($key, $key_array))
                continue;

            $weather_array[$key] = $value;
        }

        if (count($weather_array) == 0)
            return "该城市天气实时信息为空(03)";

        Yii::app()->cache->set("C_WEATHER_" . bin2hex($this->cityId), $weather_array, $weather_array['cache_expire_time']);
        return $weather_array;
    }

    /**
     * 天气预报手机端接口
     * @param string $type
     * @param array $para
     * @author Liupeng <lp@tongda2000.com>
     */
    public function mAPI($type, $para = null) {
        if ($type == 'getWeather') {
            $return = array();
            $this->cityId = $this->name2id($para['city_name']);
            $data = $this->getWeatherById($this->cityId, false);
            $current = $this->getDailyWeatherCurrent();
            date_default_timezone_set('PRC');
            if (is_array($data)) {
                //拼装数据
                $return[0] = array(
                    'date' => date('m月d日', time()),
                    'img' => $this->weatherIconMobile[$data['weather1_d']]['s'],
                    'weather' => $data['weather1'],
                    'temperature' => self::revertTemp($data['temp1'], false),
                    'wind' => $data['wind1_d'],
                    'bg' => $this->weatherIconMobile[$data['weather1_d']]['b'],
                    'mtemperature' => self::revertTemp($data['temp1'], false),
                        //      'mtemperature' => $current['temp'] ? $current['temp'] . "℃" : 'N/A'
                );

                $return[1] = array(
                    'date' => '明天',
                    'img' => $this->weatherIconMobile[$data['weather2_d']]['s'],
                    'weather' => $data['weather2'],
                    'temperature' => self::revertTemp($data['temp2']),
                    'wind' => $data['wind2_d']
                );
                $return[2] = array(
                    'date' => TUtil::getWeekDay(strtotime('+2 day', time())),
                    'img' => $this->weatherIconMobile[$data['weather3_d']]['s'],
                    'weather' => $data['weather3'],
                    'temperature' => self::revertTemp($data['temp3']),
                    'wind' => $data['wind3_d']
                );
                $return[3] = array(
                    'date' => TUtil::getWeekDay(strtotime('+3 day', time())),
                    'img' => $this->weatherIconMobile[$data['weather4_d']]['s'],
                    'weather' => $data['weather4'],
                    'temperature' => self::revertTemp($data['temp4']),
                    'wind' => $data['wind4_d']
                );
                return $return;
            } else {
                return $data;
            }
        }
    }

    /**
     * 处理天气预报返回的逆序温度
     * @param string $temp
     * @author Liupeng <lp@tongda2000.com>
     */
    public static function revertTemp($temp, $replace = true) {
        $arrTemp = explode('~', $temp);
        if ($replace)
            return str_replace(array('℃', '~'), array('°', '/'), $arrTemp[1] . '~' . $arrTemp[0]);
        else
            return $arrTemp[1] . '~' . $arrTemp[0];
    }

